mapper.update:
  	update connect_lushu_upload
	set
	 uploadid=#{uploadid,jdbcType=CHAR}
	where lushuid=#{lushuid,jdbcType=CHAR}

mapper.select:
SELECT m.* FROM map_school_lushu m where 1=1
and (m.name like CONCAT('%',#{name},'%') or m.ename like CONCAT('%',#{name},'%'))
and m.area=#{area}
and m.fabu=#{fabu}
order by m.fabu desc, m.startTime desc

mapper.update:
  	update map_school_lushu 
	set fabu = #{fabu}
	where id = #{id}
