mapper.select:
select count(id) from map_dir where enabled='1' and
parent_id is null
parent_id = #{parentId,jdbcType=CHAR}

mapper.update:
update map_dir set name = #{name,jdbcType=VARCHAR}, seq = #{seq,jdbcType=INTEGER}, enabled = #{enabled,jdbcType=CHAR},
parent_id = #{parent.id,jdbcType=CHAR}
parent_id = null
where id = #{id,jdbcType=CHAR}

mapper.select:
        select 

        from map_dir where 1=1 

		      and name like CONCAT('%',#{name},'%')


		      and seq = #{seq,jdbcType=INTEGER}


		      and enabled = #{enabled,jdbcType=CHAR}


		      and parent_id = #{parent.id,jdbcType=CHAR}

		 order by seq asc

		select 
        count(*)
        from map_dir where 1=1 

		      and name like CONCAT('%',#{name},'%')


		      and seq = #{seq,jdbcType=INTEGER}


		      and enabled = #{enabled,jdbcType=CHAR}


		      and parent_id = #{parent.id,jdbcType=CHAR}
