mapper.select:
        select 

        from map_layer where 1=1 

		      and name like CONCAT('%',#{name},'%')


		      and layerType = #{layerType,jdbcType=VARCHAR}


		      and seq = #{seq,jdbcType=INTEGER}


		      and display = #{display,jdbcType=BIT}


		      and status = #{status,jdbcType=BIT}


		      and deleted = #{deleted,jdbcType=BIT}


		      and dirId = #{dirId,jdbcType=CHAR}


		      and angle = #{angle,jdbcType=VARCHAR}

		 order by seq asc

		select 
        count(*)
        from map_layer where 1=1 
			 and
                name = #{name,jdbcType=VARCHAR},

             and
                ename = #{ename,jdbcType=VARCHAR}


		      and layerType = #{layerType,jdbcType=VARCHAR}

             and
                alias = #{alias,jdbcType=VARCHAR}

             and
                url = #{url,jdbcType=VARCHAR}

             and
                source = #{source,jdbcType=VARCHAR}

             and
                layerType = #{layerType,jdbcType=VARCHAR}

             and
                featureNS = #{featureNS,jdbcType=VARCHAR}

             and
                featureType = #{featureType,jdbcType=VARCHAR}

             and
                version = #{version,jdbcType=VARCHAR}

             and
                resolution = #{resolution,jdbcType=LONGVARCHAR}

             and
                projection = #{projection,jdbcType=VARCHAR}

             and
                bound = #{bound,jdbcType=LONGVARCHAR}

             and
                extent = #{extent,jdbcType=VARCHAR}

             and
                layers = #{layers,jdbcType=VARCHAR}

             and
                tilesize = #{tilesize,jdbcType=VARCHAR}

             and
                tileorigin = #{tileorigin,jdbcType=VARCHAR}

             and
                angle = #{angle,jdbcType=VARCHAR}

             and
                seq = #{seq,jdbcType=INTEGER}

             and
                display = #{display,jdbcType=BIT}

             and
                status = #{status,jdbcType=BIT}

             and
                deleted = #{deleted,jdbcType=BIT}

             and
                addDate = #{addDate,jdbcType=TIMESTAMP}

             and
                remark = #{remark,jdbcType=VARCHAR}

             and
                dirId = #{dirId,jdbcType=CHAR}

        select 

        from map_layer where 1=1 

		      and name like CONCAT('%',#{name},'%')


		      and layerType = #{layerType,jdbcType=VARCHAR}


		      and seq = #{seq,jdbcType=INTEGER}


		      and alias = #{alias,jdbcType=VARCHAR}


		      and display = #{display,jdbcType=BIT}


		      and status = #{status,jdbcType=BIT}


		      and deleted = #{deleted,jdbcType=BIT}


		      and dirId = #{dirId,jdbcType=CHAR}


		      and angle = #{angle,jdbcType=VARCHAR}

		 order by seq asc
