mapper.select:
select 

from a_pass
where start = #{start,jdbcType=CHAR}
  and end = #{end,jdbcType=CHAR}

select 

from a_pass
where 1=1

        and start = #{start,jdbcType=LONGVARCHAR}


        and end = #{end,jdbcType=LONGVARCHAR}


        and path = #{path,jdbcType=LONGVARCHAR}


        and length = #{length,jdbcType=DOUBLE}


        and area = #{area,jdbcType=CHAR}


        and cycle_way = #{cycleWay,jdbcType=CHAR}


        and foot_way = #{footWay,jdbcType=CHAR}


        and drive_way = #{driveWay,jdbcType=CHAR}


        and traffic_control = #{trafficControl,jdbcType=CHAR}

mapper.delete:
delete from a_pass
where start = #{start,jdbcType=CHAR}
  and end = #{end,jdbcType=CHAR}
