mapper.select:
select 

from map_presentation a
where id = #{id,jdbcType=CHAR}

mapper.insert:
insert into map_presentation (id, title, subhead, 
  author, author_org, create_date, 
  area_id, used, commentary, 
  video_id, image_id, location,description
  )
values (#{id,jdbcType=CHAR}, #{title,jdbcType=VARCHAR}, #{subhead,jdbcType=VARCHAR}, 
  #{author,jdbcType=VARCHAR}, #{authorOrg,jdbcType=VARCHAR}, #{createDate,jdbcType=TIMESTAMP}, 
  #{areaId,jdbcType=CHAR}, #{used,jdbcType=BIT}, #{commentary,jdbcType=VARCHAR}, 
  #{videoId,jdbcType=CHAR}, #{imageId,jdbcType=CHAR}, GEOMFROMTEXT(#{location}),#{description,jdbcType=VARCHAR}
  )
