mapper.update:
  	update connect_school_upload
	set
	 uploadId=#{uploadId,jdbcType=CHAR}
	where schoolId=#{schoolId,jdbcType=CHAR}

mapper.select:
SELECT m.* FROM map_schoolactivity m where 1=1
and (m.name like CONCAT('%',#{name},'%') or m.ename like CONCAT('%',#{name},'%'))
and m.area=#{area}
and m.fabu = #{fabu}
order by m.fabu desc, m.speaktime desc

mapper.update:
  	update map_schoolactivity 
	set fabu = #{fabu},
		fabuState = #{fabuState}
	where id = #{id}
