mapper.select:
	select * from sys_backup 
	  	where 1=1 

  		and file_name like CONCAT('%',#{fileName,jdbcType=VARCHAR},'%')


  		and file_url = #{fileUrl,jdbcType=VARCHAR}


  		and deleted = #{deleted,jdbcType=BIT}


  		and db_type = #{dbType,jdbcType=VARCHAR}


		and DATE_FORMAT(backup_date, '%Y-%m-%d %H:%i:%S')>=  DATE_FORMAT(#{startTime}, '%Y-%m-%d %H:%i:%S')


		and DATE_FORMAT(backup_date, '%Y-%m-%d %H:%i:%S')<  DATE_FORMAT(#{endTime}, '%Y-%m-%d %H:%i:%S')

  	order by backup_date desc
