mapper.select:
	select * from sys_file s 
	  	where 1=1 

  		and module = #{module,jdbcType=VARCHAR}


  		and real_name = #{realName,jdbcType=VARCHAR}


  		and file_name like CONCAT('%',#{fileName,jdbcType=VARCHAR},'%')


  		and file_url = #{fileUrl,jdbcType=VARCHAR}


  		and file_size = #{fileSize,jdbcType=INTEGER}


  		and file_format in 

            #{format}



		and DATE_FORMAT(upload_date, '%Y-%m-%d %H:%i:%S')>=  DATE_FORMAT(#{startTime}, '%Y-%m-%d %H:%i:%S')


		and DATE_FORMAT(upload_date, '%Y-%m-%d %H:%i:%S')<  DATE_FORMAT(#{endTime}, '%Y-%m-%d %H:%i:%S')


  		and deleted = #{deleted,jdbcType=BIT}

  	order by upload_date desc
