mapper.select:
    select count(ip) from( 
		select distinct ip from sys_tracklog where access_time >= #{startTime,jdbcType=TIMESTAMP} and access_time < #{endTime,jdbcType=TIMESTAMP}
	) log_temp

select ip,count(*) as count,sum(stay_time) as sumTime from sys_tracklog 
where 1=1 

	and access_time >= #{startTime,jdbcType=TIMESTAMP}


	and access_time < #{endTime,jdbcType=TIMESTAMP}

group by ip

select page,count(*) as count,sum(stay_time) as sumTime from sys_tracklog 
where 1=1 

	and access_time >= #{startTime,jdbcType=TIMESTAMP}


	and access_time < #{endTime,jdbcType=TIMESTAMP}

group by page

select DATE_FORMAT(access_time,'%Y%m%d') date,count(id) count,sum(stay_time) sumTime 
from sys_tracklog  
where 1=1

		and access_time >= #{startTime,jdbcType=TIMESTAMP}


		and access_time < #{endTime,jdbcType=TIMESTAMP}

group by date ORDER BY date

	select 

    from sys_tracklog
    where 1=1

  			and access_time >= #{startTime,jdbcType=TIMESTAMP}


  			and access_time < #{endTime,jdbcType=TIMESTAMP}

  	ORDER BY access_time desc

mapper.insert:

    select  replace(uuid(),'-','')   from dual  

insert into sys_tracklog (id, session_id, ip, 
  page,user_name, access_time, stay_time,device_type,os,browser,browser_version
  )
values (#{id,jdbcType=CHAR}, #{sessionId,jdbcType=VARCHAR}, #{ip,jdbcType=VARCHAR}, 
  #{page,jdbcType=VARCHAR},#{userName,jdbcType=VARCHAR}, #{accessTime,jdbcType=TIMESTAMP}, 
  #{stayTime,jdbcType=LONGVARCHAR},#{deviceType,jdbcType=VARCHAR},#{os,jdbcType=VARCHAR},
  #{browser,jdbcType=VARCHAR},#{browserVersion,jdbcType=VARCHAR}
  )
