mapper.select:
SELECT m.* FROM map_teachers m where m.deleted=0
and m.categoryType= #{categoryType,jdbcType=CHAR}
and m.area=#{area}
and m.name like CONCAT('%',#{name},'%')
order by updateTime desc

mapper.update:
  	update connect_teachers_upload
	set
	 uploadId=#{uploadId,jdbcType=CHAR}
	where teachersId=#{teachersId,jdbcType=CHAR}
