mapper.update:
  	update connect_vr_upload
	set
	 uploadId=#{uploadId,jdbcType=CHAR}
	where vrId=#{vrId,jdbcType=CHAR}

mapper.select:
SELECT m.* FROM map_vr m where 1=1
and (m.name like CONCAT('%',#{name},'%') or m.ename like CONCAT('%',#{name},'%'))
and m.area=#{area}
and m.vrtype=#{vrtype,jdbcType=CHAR}
